responses = []

for filename in os.listdir(FOLDER_PATH):
    filepath = os.path.join(FOLDER_PATH, filename)
    if os.path.isfile(filepath):
        
        with open(filepath, "rb") as file:
            files = {"file": (filename, file, "application/octet-stream")}
            response = requests.post(API_URL, headers=HEADERS, files=files)
            try:
                response_json = response.json()
            except ValueError:
                response_json = {"error": response.text}

            print(response_json)
            response_data = {
                "filename": filename,
                "status_code": response.status_code,
                "response_text": "https://cdn-gov-cloud.ai/" + response_json.get("cdnUrl", "")
            }
            responses.append(response_data)
            
            if response.status_code == 200:
                print(f"Uploaded {filename}")
            else:
                print(f"Failed to upload {filename}. Status code: {response.status_code}. Response: {response.text}")

# Save responses to JSON file
with open(RESPONSES_FILE, "w") as json_file:
    json.dump(responses, json_file, indent=2)

print(f"Responses saved to {RESPONSES_FILE}")
